$(document).ready(function() {
  // Typing effect variables
  let text = "Loading NEX GEN AI...";
  let i = 0;

  // Typing animation function
  function typeWriter() {
    if (i < text.length) {
      $("#loading-text").append(text.charAt(i));
      i++;
      setTimeout(typeWriter, 80); // Typing speed
    } else {
      // After typing is complete, wait a bit then fade out
      setTimeout(function() {
        $("#loading-screen").fadeOut(1000, function() {
          $(this).remove();
        });
      }, 1000); // Wait 1 second after typing
    }
  }

  // Clear any text initially and start typing
  $("#loading-text").text('');
  typeWriter();
});
