$(document).ready(function() {
  // Animate the logo appearance
  $("#loading-logo").css({ opacity: 0, transform: "scale(0.8)" });
  
  setTimeout(function() {
    $("#loading-logo").css({ opacity: 1, transform: "scale(1)" });
  }, 500); // Fade and scale up after 0.5s

  // After a short wait, hide loading screen
  setTimeout(function() {
    $("#loading-screen").fadeOut(800, function() {
      $(this).remove();
    });
  }, 2500); // 2.5 seconds later, fade out
});
